"use strict";

(function ($) {
  $(function () {
    var site = {
      dataItem: 0,
      lengthItemPortfolio: $('.portfolio__item').length,
      menuHandler: function menuHandler() {
        $('.navigation__link').each(function () {
          var _this = this;

          $(this).click(function () {
            var idSection = $(_this)[0].hash;
            $('#menu').prop('checked', false);
            $('body, html').animate({
              scrollTop: $(idSection).offset().top
            }, 600);
          });
        });
      },
      formHandler: function formHandler() {
        $('#form-mail').submit(function (e) {
          e.preventDefault();
          var data = $(this).serialize();
          console.log(data);
          $.ajax({
            method: "POST",
            data: data,
            url: "/send.php",
            success: function success(res) {
              console.log(res);
            }
          });
        });
      },
      carouselInit: function carouselInit() {
        var self = this;
        $('.portfolio__item').each(function (index) {
          if (index === self.dataItem) {
            $(this).show();
          } else {
            $(this).hide();
          }
        });
      },
      carouselHandler: function carouselHandler() {
        var self = this;
        $('.portfolio__btn').click(function () {
          if ($(this).hasClass('portfolio__btn--left')) {
            if (self.dataItem === 0) {
              self.dataItem = self.lengthItemPortfolio - 1;
              self.carouselInit();
            } else {
              self.dataItem--;
              self.carouselInit();
            }
          } else {
            if (self.dataItem === self.lengthItemPortfolio - 1) {
              self.dataItem = 0;
              self.carouselInit();
            } else {
              self.dataItem++;
              self.carouselInit();
            }
          }

          $('body, html').animate({
            scrollTop: $('#portfolio').offset().top
          }, 600);
        });
      },
      ieHandler: function ieHandler() {
        if ($('.footer').css('background-color') === 'rgb(204, 204, 204)') {
          $('body').html('<h1 style="font-size: 5rem;font-weight: 400;margin: 3rem;">Your browser is deprecated. Please use a newer browser.</h1>');
        }
      },
      init: function init() {
        this.menuHandler();
        this.formHandler();
        this.carouselInit();
        this.carouselHandler();
        this.ieHandler();
      }
    };
    site.init();
  });
})(jQuery);
